	private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) throws SQLException {//edit
		String name=jTextField1.getText();
		String address=jTextField3.getText();
		String phone=jTextField4.getText();
		Person person=new Person();
		person.setName(name);
		person.setAddress(address);
		person.setPhone(phone);
		boolean b=Data.Edit(person);
		if (b)
			jTextField2.setText("YES!! edited");
		else
			jTextField2.setText("this name does not exist.");
		jTextField1.setText(null);
		jTextField3.setText(null);
		jTextField4.setText(null);
	}

	private void jButton3ActionPerformed(java.awt.event.ActionEvent evt)//delete
			throws SQLException {
		String name = jTextField1.getText();
		boolean b = Data.Delete(name);
		if (b)
			jTextField2.setText("Yes!!! Deleted");
		else
			jTextField2.setText("this name does not exist.");
		jTextField1.setText(null);
		jTextField3.setText(null);
		jTextField4.setText(null);
	}

	private void jButton2ActionPerformed(java.awt.event.ActionEvent evt)//Add
			throws SQLException {
		Person person = new Person();
		String name = jTextField1.getText();
		String address = jTextField3.getText();
		String phone = jTextField4.getText();
		person.setName(name);
		person.setAddress(address);
		person.setPhone(phone);
		Data.Add(person);
		jTextField2.setText("YES!! Added ");
		jTextField1.setText(null);
		jTextField3.setText(null);
		jTextField4.setText(null);
	}

