	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt)//search
			throws SQLException {
		String str="";
		String name = jTextField1.getText();
		persons = Data.Search(name);
		if (name.equals("")) {
			jTextField2.setText("Please Entered name of your search");
		}else if(persons.size()==0){
			jTextField2.setText("this name does not exist.");
		}else {

			//persons = Data.Search(name);
			for (int i = 0; i < persons.size(); i++) {
				str = str + "*********";
				str = str + persons.get(i).getName();
				str = str + "    Address: ";
				str = str + persons.get(i).getAddress();
				str = str + "    phone: ";
				str = str + persons.get(i).getPhone();
			}
			jTextField2.setText(str);
		}
		jTextField1.setText(null);
		jTextField3.setText(null);
		jTextField4.setText(null);
	}

